import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './Main.css';

function Navbar() {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [searchQuery, setSearchQuery] = useState("");

  // List of dog names for demonstration
  const dogNames = ["Buddy", "Max", "Bailey", "Charlie", "Lucy", "Cooper"]; // Add more dog names as needed

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  // Filter dog names based on search query
  const filteredDogNames = dogNames.filter(name =>
    name.toLowerCase().includes(searchQuery)
  );

  return (
    <nav className='nav'>
      <a href="#" className="nav__brand">Dog Rating</a>
      <ul className={active}>
        {/* Mapping through filtered dog names instead of menu items */}
        {filteredDogNames.map((dogName, index) => (
          <li key={index} className="nav__item">
            <Link to={`/dog/${index}`} className="nav__link">{dogName}</Link>
          </li>
        ))}
      </ul>
      <div className="search-box">
        <input
          type="text"
          placeholder="Search"
          value={searchQuery}
          onChange={handleSearch}
        />
        <img src="" alt=""/>
      </div>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
