import React, { useState } from "react";
import { Link } from 'react-router-dom';
import './Main.css';

function Navbar() {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [searchQuery, setSearchQuery] = useState("");
  const [dogs, setDogs] = useState([]); // State for storing dogs data

  // Dummy data for demonstration
  const dummyDogData = [
    { id: 1, name: "Buddy" },
    { id: 2, name: "Max" },
    { id: 3, name: "Bailey" },
    // Add more dogs as needed
  ];

  // Function to fetch dogs data (replace with your actual data fetching logic)
  const fetchDogsData = () => {
    // Simulate fetching data from an API
    setTimeout(() => {
      setDogs(dummyDogData);
    }, 500); // Simulate 0.5 second delay
  };

  // Fetch dogs data when the component mounts
  useEffect(() => {
    fetchDogsData();
  }, []);

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value.toLowerCase());
  };

  // Filter dogs based on search query
  const filteredDogs = dogs.filter(dog =>
    dog.name.toLowerCase().includes(searchQuery)
  );

  return (
    <nav className='nav'>
      <a href="#" className="nav__brand">Dog Rating</a>
      <ul className={active}>
        {/* Mapping through filtered dogs instead of menu items */}
        {filteredDogs.map((dog) => (
          <li key={dog.id} className="nav__item">
            <Link to={`/dog/${dog.id}`} className="nav__link">{dog.name}</Link>
          </li>
        ))}
      </ul>
      <div className="search-box">
        <input
          type="text"
          placeholder="Search"
          value={searchQuery}
          onChange={handleSearch}
        />
        <img src="" alt=""/>
      </div>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
